import "./Homepage.css";
import Header from "../Header/Header";
import { useState } from "react";

const Homepage = () => {
  const [title, setTitle] = useState("all");
  const [animate, setAnimate] = useState(true);

  const ClickHandler = (value) => {
    setTitle(value);
    setAnimate(!animate);
    console.log(value);
  };

  const AnimationHandler = () => {
    setAnimate(!animate);
  };

  return (
    <div>
      <Header /> {/* 6/2 Header 컴포넌트 재사용 위해서 분리 */}
      <div className="introduce">
        <h2>choi yunsuk</h2>
        <h4>2017.03~ &nbsp;&nbsp;&nbsp; KYUNGHEE UNIV. COMPUTER ENGINEERING</h4>
      </div>
      <h1
        className={animate ? "fadein tags" : "tags"}
        onAnimationEnd={AnimationHandler}
      >
        {title}
      </h1>
      <Button />
      {/* <p>Today : 1 &nbsp; / &nbsp; Total : 10</p> */}
      <div className="cardcontainer">
        <h3 className="postcard">
          PROJECT #1-3
          <br />: HTML RENDERING
        </h3>
        <h2 className="postcard">
          PROJECT #1-2
          <br />: ENVIRONMENT CONFIG
        </h2>
        <h2 className="postcard">
          WHAT IS
          <br />
          GIT / GITHUB ?
        </h2>
        <h2 className="postcard">
          PROJECT #1-1
          <br />: DEV MY OWN BLOG
        </h2>
        <h1 className="postcard">aaa</h1>
        <h1 className="postcard">aaa</h1>
        <h1 className="postcard">aaa</h1>
        <h1 className="postcard">aaa</h1>
        <h1 className="postcard">aaa</h1>
      </div>
      Choigonyok
    </div>
  );
};

export default Homepage;
