import "./Card.css";
import { useNavigate } from "react-router-dom";

const Card = (props) => {
  const navigate = useNavigate();

  const handleClick = () => {

    // 버튼 클릭 시 특정 URL로 이동
    navigate("/post/"+props.postdata.Id);
  };
  return (
    <div>
      {props.postdata && (
        <div className="cardcontainer">
          {props.postdata.map((item, index) => (
            <div>
              <h2 className="postcard" onClick={handleClick}>
                <div>
                  <img
                    className="postcard-image"
                    alt="my"
                    src={item.ImagePath}
                  />
                </div>
                <div className="postcard-text">
                  <p>{item.Title}</p>
                </div>
                <div className="postcard-tag">
                  <p>{item.Tag}</p>
                </div>
                <div className="postcard-date">
                  <p className="postcard-date__box">{item.Datetime}</p>
                </div>
              </h2>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Card;
