import img4 from "../Assets/img4.jpeg";
import img5 from "../Assets/img5.jpeg";
import img6 from "../Assets/img6.jpeg";
import img7 from "../Assets/img7.jpeg";
import img8 from "../Assets/img8.jpeg";
import img9 from "../Assets/img9.jpeg";
import { useNavigate } from "react-router-dom";

const Card = (props) => {

  const navigate = useNavigate();

  const handleClick = () => {
    // 버튼 클릭 시 특정 URL로 이동
    navigate("/post");
  };
  return (
    <div className="cardcontainer">
      {props.postdata && <div>{props.postdata.map((item, index) =>

        <div>
          <div>
            <h2 className="postcard" onClick={handleClick}>
              <div>
                <img className="postcard-image" alt="my" src={item.ImagePath}/>
              </div>
              <div className="postcard-text">
                <p>{item.Title}</p>
              </div>
              <div className="postcard-tag">
                <p>{item.Tag}</p>
              </div>
              <div className="postcard-date">
                <p className="postcard-date__box">{item.Datetime}</p>
              </div>
            </h2>
          </div>
        </div>
      )}</div>}
