import Header from "../Header/Header";
import ContentsViewer from "../UI/ContentsViewer";
import EditorBox from "../UI/EditorBox";
import abc from "../assets/KakaoTalk_Photo_2022-09-27-12-53-39 003.png";
import "./Postpage.css";
import { useState } from "react";

const Postpage = () => {
  const [inputText, setInputText] = useState('');
  const Handler = (e) => {
    setInputText(e.target.value);
    console.log(inputText);
  }

  return (
    <div>
      <Header />
      <br />
      <br />
      <br />
      <br />
      <div className="image-container">
        <input type="button" className="postbutton" value="BEFORE" />
        <img className="image" alt="my" src={abc} />
        <input type="button" className="postbutton" value="AFTER" />
      </div>
      <p>tag buttons</p>
      <div className="post-title">
        <p className="post-title__item">PROJECT #1-1 DEVELOPING TECH BLOG !</p>
      </div>
      <div>
        <EditorBox onChange={Handler}/>
      </div>



      <div className="post-body">
        <div className="post-body__item">
        <ContentsViewer  contents={inputText}/>
        </div>
      </div>
      <p>index</p>
      <p>이전/다음 포스트버튼</p>
      <p>related posts : 태그가 같은 post 배치</p>
      <p>load more : 6개만 표시하고 나머지는 load more 클릭하면 표시</p>
    </div>
  );
};

export default Postpage;
